import { useContext } from "react";
import CollectionContext from "../../../store/collection-context";
const SpecificSearch = () => {
  const collectionsCtx = useContext(CollectionContext);
  return (
    <div className="row text-center">
      {collectionsCtx.tempCollection.map((NFT, key) => {
        const index = marketplaceCtx.offers
          ? marketplaceCtx.offers.findIndex((offer) => offer.id === NFT.id)
          : -1;
        const owner =
          index === -1 ? NFT.owner : marketplaceCtx.offers[index].user;
        const price =
          index !== -1
            ? formatPrice(marketplaceCtx.offers[index].price).toFixed(2)
            : null;

        return (
          <div key={key} className="col-md-2 m-3 pb-3 card border-info">
            <div className={"card-body"}>
              <h5 className="card-title">{NFT.title}</h5>
            </div>
            <img
              src={`https://ipfs.infura.io/ipfs/${NFT.img}`}
              className="card-img-bottom"
              alt={`NFT ${key}`}
            />
            <p className="fw-light fs-6">{`${owner.substr(
              0,
              7
            )}...${owner.substr(owner.length - 7)}`}</p>
            {index !== -1 ? (
              owner !== web3Ctx.account ? (
                <div className="row">
                  <div className="d-grid gap-2 col-5 mx-auto">
                    <button
                      onClick={buyHandler}
                      value={index}
                      className="btn btn-success"
                    >
                      BUY
                    </button>
                  </div>
                  <div className="col-7 d-flex justify-content-end">
                    <img
                      src={eth}
                      width="25"
                      height="25"
                      className="align-center float-start"
                      alt="price icon"
                    ></img>
                    <p className="text-start">
                      <b>{`${price}`}</b>
                    </p>
                  </div>
                </div>
              ) : (
                <div className="row">
                  <div className="d-grid gap-2 col-5 mx-auto">
                    <button
                      onClick={cancelHandler}
                      value={index}
                      className="btn btn-danger"
                    >
                      CANCEL
                    </button>
                  </div>
                  <div className="col-7 d-flex justify-content-end">
                    <img
                      src={eth}
                      width="25"
                      height="25"
                      className="align-center float-start"
                      alt="price icon"
                    ></img>
                    <p className="text-start">
                      <b>{`${price}`}</b>
                    </p>
                  </div>
                </div>
              )
            ) : owner === web3Ctx.account ? (
              <form
                className="row g-2"
                onSubmit={(e) => makeOfferHandler(e, NFT.id, key)}
              >
                <div className="col-5 d-grid gap-2">
                  <button type="submit" className="btn btn-warning">
                    OFFER
                  </button>
                </div>
                <div className="col-7">
                  <input
                    type="number"
                    step="0.01"
                    placeholder="ETH..."
                    className="form-control"
                    ref={priceRefs.current[key]}
                  />
                </div>
              </form>
            ) : (
              <p>
                <br />
              </p>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default SpecificSearch;
